import React from 'react'
import { Link } from 'react-router-dom'

const Feed = ({ books, value }) => {
    const image = 'https://static.productionready.io/images/smiley-cyrus.jpg'

    let filterTitle = books.filter((search) => {
        return search.title.toLowerCase().includes(value.toLowerCase())
    })

    return (
        <div>
            {filterTitle.map((book, index) => (
                <div className='article-preview' key={index}>
                    <div className='article-meta'>
                        <Link to={`/profiles/${book.book_id}`}>
                            <img src={image} />
                        </Link>
                        <div className='info'>
                            <Link to={`/profiles/${book.book_id}`} className='author'>
                                {book.title}
                            </Link>
                            <span className="date">{book.rating} rating</span>
                        </div>
                    </div>
                    <div className="preview-link">
                        {/* <h1>{book.author.name}</h1> */}
                        <p>{book.description}</p>
                        <span>Read more...</span>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Feed