import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import Dialog from 'components/popup/Dialog';
import useFetch from 'hooks/useFetch'
import { useEffect } from 'react'


const FeedAuthor = ({ books,value }) => {
    const [modalShow, setModalShow] = useState(false);
    const [helper, setHelperClick] = useState('')
     

    const image = 'https://static.productionready.io/images/smiley-cyrus.jpg'


    const apiURL = `/book/${helper}`
    const [{ response, isLoading, error }, doFetch] = useFetch(apiURL)

    useEffect(() => {
        doFetch()
    }, [doFetch])

    let filterName = books.filter((search) => {
        return search.author.name.toLowerCase().includes(value.toLowerCase())
    })

    return (
        <div>
            {filterName.map((book, index) => (
                <div className='article-preview' key={index}>
                    <div className='article-meta'>
                        <Link to={`/profiles/${book.book_id}`}>
                            <img src={image} />
                        </Link>
                        <div className='info'>
                            <div
                                className='author'
                                variant="primary"
                                onClick={() => setHelperClick(book.book_id, setModalShow(true))}
                            >
                                {book.author.name}
                            </div>
                            {modalShow && !isLoading && response && (
                                <Dialog
                                    modalShow={modalShow}
                                    onClose={() => setModalShow(false)}
                                    response={response}
                                    helper={helper}
                                >
                                </Dialog>
                            )}
                            <span className="date">{book.rating} rating</span>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    )
}



export default FeedAuthor

