import React from 'react'
import {Switch, Route} from 'react-router-dom'

import GlobalFeed from 'pages/globalFeed'
import Article from 'pages/article'
import AuthorFeed from 'pages/authorFeed/index'
 

const Routes = () => {
  return (
    <Switch>
      <Route path="/" component={GlobalFeed} exact />
      <Route path="/author" component={AuthorFeed}  /> 
      <Route path="/profiles/:bookId" component={Article} />
      <Route path="/author/:bookId" component={AuthorFeed} />
    </Switch>
  )
}
export default Routes
